    <section class="programs" id="programs">
        <script>
            const setValue = () => {
                const product       = parseInt(document.querySelector('.programs__list .active')
                                                   .getAttribute('data-activate')) + 1
                const daysNum       = document.querySelector('.category.main .dropdown .select span')
                    .innerHTML
                    .split(' ')
                    .splice(1)
                    .join(' ')
                const daysNumFather = document.querySelector('.category.father .dropdown .select span')
                    .innerHTML
                    .split(' ')
                    .splice(1)
                    .join(' ')
                const order         = document.querySelector('.category.main .dropdown .select span')
                    .innerHTML
                    .slice(3, 5)
                    .replace(' ', '')
                const orderFather   = document.querySelector('.category.father .dropdown .select span')
                    .innerHTML
                    .slice(3, 5)
                    .replace(' ', '')

                let msgMain    = $('#orderForm').serialize()
                let msgDetails = `&days=${daysNum}&product=${product}&order=${order}&father=${daysNumFather}&orderFather=${orderFather}`;

                if (promoInputSurround.classList.contains('active')) {
                    msgDetails += `&sale=50`
                }

                document.querySelector("input[name='frontpadMain']").value    = msgMain
                document.querySelector("input[name='frontpadDetails']").value = msgDetails

                let descr

                document.querySelector("input[name='value']").value = sumTotal == 0 ? 3000 : sumTotal
                if (extraTotal == 0) {
                    descr = (document.querySelector('.category.main p').innerHTML + ' ' + document.querySelector('.category.main span').innerHTML).replace(/&nbsp;/g, " ")
                        .replace(/<br>/g, ' ')
                } else {
                    descr = (document.querySelector('.category.main p').innerHTML + ' ' + document.querySelector('.category.main span').innerHTML + ' и ' + document.querySelector('.category.father p').innerHTML + ' ' + document.querySelector('.category.father span').innerHTML).replace(/&nbsp;/g, ' ')
                        .replace(/<br>/g, ' ')
                }
                document.querySelector("input[name='description']").value = descr
            }
        </script>

